// code for the timer
let counterValue = 0;
const counterElement = document.getElementById("counter");

function incrementCounter() {
    counterValue++;
    counterElement.innerText = counterValue;
}

// Increment the counter every second
setInterval(incrementCounter, 1000);
// manual increment and decrement
const plusButton = document.getElementById("plus");
const minusButton = document.getElementById("minus");

plusButton.addEventListener("click", () => {
    counterValue++;
    counterElement.innerText = counterValue;
});

minusButton.addEventListener("click", () => {
    counterValue--;
    counterElement.innerText = counterValue;
});
//code for liking numbers
const likeButton = document.getElementById("like");
const likesList = document.getElementById("likesList");

likeButton.addEventListener("click", () => {
    const li = document.createElement("li");
    li.innerText = `Liked ${counterValue} at ${new Date().toLocaleTimeString()}`;
    likesList.appendChild(li);
});
//code for pausing and resuming the counter
const pauseButton = document.getElementById("pause");
let isPaused = false;

pauseButton.addEventListener("click", () => {
    isPaused = !isPaused;
    if (isPaused) {
        clearInterval(interval); 
        pauseButton.innerText = "Resume";
        plusButton.disabled = true;
        minusButton.disabled = true;
        likeButton.disabled = true;
    } else {
        interval = setInterval(incrementCounter, 1000); // Resume the timer
        pauseButton.innerText = "Pause";
        plusButton.disabled = false;
        minusButton.disabled = false;
        likeButton.disabled = false;
    }
});
